import React, { useState, useEffect, useRef, SetStateAction } from 'react'
import DateDisplay from '../components/DateDisplay'
import LandingSection from '../components/landing_section/LandingSection'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Features from '../components/features/Features';
import About from '../components/about/About';
import WorkedWith from '../components/worked_with/WorkedWith';
import Footer from '../components/footer/Footer';
import Header from '../components/nav/Header';

const HomePage: React.FC = () => {
    const [scrollPosition, setScrollPosition] = useState<SetStateAction<number>>();
    const scrollView = useRef<null | HTMLDivElement>(null)

    const handleScroll = (e: any) => {
        console.log("Scroll called")
        const div_ = scrollView.current
        const position = div_?.scrollTop;
        setScrollPosition(position);

        console.log("Scroll position is "+scrollPosition)
        console.log(scrollView.current?.scrollTop)
    };

    useEffect(() => {
        

        const div_ = scrollView.current
        console.log("Adding event listener")
        div_?.addEventListener('wheel', handleScroll, { passive: true });
    
        return () => {
            div_?.removeEventListener('wheel', handleScroll);
        };
    }, []);

    return (
        <div style={{ position: 'relative', display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
            <div ref={scrollView} onScroll={handleScroll} ></div>
            <Header />
            <LandingSection />
            <About />
            <Features />
            <WorkedWith />
            <Footer />
        </div>
    )
}

export default HomePage
